                TriggerKeyword = keyword,
                TriggerType = type,
                ReplyContent = replyText,
                FlowName = flowName,
                MessageLogId = messageLogId,
                TriggeredAt = DateTime.UtcNow
            };

            _context.AutoReplyLogs.Add(log);
            await _context.SaveChangesAsync();
        }


