namespace xbytechat.api.Features.BusinessModule.Controllers
{
    [ApiController]
    [Route("api/[controller]")]
    public class BusinessesController : ControllerBase
    {
        private readonly IBusinessService _businessService;

        public BusinessesController(IBusinessService businessService)
        {
            _businessService = businessService;
        }



        [HttpGet("pending")]
        [Authorize(Roles = "admin,superadmin,partner")] // partners see scoped; admin/superadmin see ALL
        public async Task<IActionResult> GetPendingBusinesses()
        {
            try
            {
                // Read role/id from multiple possible claim types
                var role = User.FindFirst(ClaimTypes.Role)?.Value
                           ?? User.FindFirst("role")?.Value
                           ?? User.FindFirst("roles")?.Value
                           ?? "";

                var userId = User.FindFirst("id")?.Value
                           ?? User.FindFirst(ClaimTypes.NameIdentifier)?.Value
                           ?? User.FindFirst("sub")?.Value
                           ?? "";

                var result = await _businessService.GetPendingBusinessesAsync(role, userId);
                return Ok(ResponseResult.SuccessInfo("âœ… Pending businesses fetched successfully.", result));
            }
            catch
            {
                return StatusCode(500, ResponseResult.ErrorInfo("âŒ Failed to fetch pending businesses. Please try again later."));
            }
        }


        // âœ… Get business by ID (used for profile completion)

        [HttpGet("{id}")]
        public async Task<IActionResult> GetBusinessById(Guid id)
        {
            try
            {
                var business = await _businessService.GetByIdAsync(id);
                if (business == null)
                    return NotFound(ResponseResult.ErrorInfo("âŒ Business not found."));

                return Ok(business);
            }
            catch (Exception ex)
            {
                return StatusCode(500, ResponseResult.ErrorInfo("ðŸš¨ Failed to fetch business. " + ex.Message));
            }
        }

        [HttpPut("assigned-to/{id}")]
        public async Task<IActionResult> UpdateBusiness(Guid id, [FromBody] Business business)
        {
            if (id != business.Id)
            {
                return BadRequest(new { message = "âŒ ID mismatch." });
            }

            var result = await _businessService.UpdateBusinessAsync(business);
            if (!result.Success)
            {
                return BadRequest(result);
            }

            return Ok(result);
        }


        // ðŸŸ¢ Approve a business
        [HttpPost("approve/{id}")]
        public async Task<IActionResult> Approve(Guid id)
        {
            try
            {
                var result = await _businessService.ApproveBusinessAsync(id);

                if (result.Success)
                {
                    // âœ… Optional Success Logging
                    Log.Information("âœ… Business approved successfully. BusinessId: {BusinessId}", id);
                    return Ok(result);
                }
                else
                {
                    // âœ… Optional Warning Logging
                    Log.Warning("âš ï¸ Business approval failed. BusinessId: {BusinessId} - Message: {Message}", id, result.Message);
                    return BadRequest(result);
                }
            }
            catch (Exception ex)
            {
                // âœ… Proper Error Logging
                Log.Error(ex, "âŒ Exception occurred while approving business. BusinessId: {BusinessId}", id);

                return StatusCode(500, ResponseResult.ErrorInfo(
                    "âŒ Something went wrong while approving business. Please try again later."
                ));
            }
        }


        // ðŸ”´ Reject a business
        [HttpPost("reject/{id}")]
        public async Task<IActionResult> Reject(Guid id)
        {
            try
            {
                var result = await _businessService.RejectBusinessAsync(id);
                return result.Success ? Ok(result) : NotFound(result);
            }
            catch (Exception ex)
            {
                return StatusCode(500, ResponseResult.ErrorInfo("âŒ Failed to reject business. " + ex.Message));
            }
        }

        // ðŸŸ¡ Put a business on hold
        [HttpPost("hold/{id}")]
        public async Task<IActionResult> Hold(Guid id)
        {
            try
            {
                var result = await _businessService.HoldBusinessAsync(id);
                return result.Success ? Ok(result) : NotFound(result);
            }
            catch (Exception ex)
            {
                return StatusCode(500, ResponseResult.ErrorInfo("âŒ Failed to hold business. " + ex.Message));
            }
        }

        // ðŸ›  Complete profile after signup
        [HttpPost("profile-completion/{businessId}")]
        public async Task<IActionResult> CompleteProfile(Guid businessId, [FromBody] ProfileCompletionDto dto)
        {
            try
            {
                var result = await _businessService.CompleteProfileAsync(businessId, dto);
                return result.Success ? Ok(result) : BadRequest(result);
            }
            catch (Exception ex)
            {
                return StatusCode(500, ResponseResult.ErrorInfo("âŒ Failed to update profile. " + ex.Message));
            }
        }

        [HttpGet("approved")]
        [Authorize(Roles = "SuperAdmin")]
        public async Task<IActionResult> GetApprovedBusinesses()
        {
            var result = await _businessService.GetApprovedBusinessesAsync();
            return Ok(result);
        }

    }
}

