namespace xbytechat.api.Features.CampaignTracking.Services
{
    public class CampaignTrackingRetryService : ICampaignTrackingRetryService
    {
        private readonly AppDbContext _context;

        public CampaignTrackingRetryService(AppDbContext context)
        {
            _context = context;
        }

        // ‚úÖ Public method: Retry a single failed log
        public async Task<bool> RetrySingleAsync(Guid logId)
        {
            return await RetrySendLogAsync(logId);
        }

        // ‚úÖ Public method: Retry all failed logs in a campaign
        public async Task<int> RetryFailedInCampaignAsync(Guid campaignId)
        {
            return await RetryAllFailedInCampaignAsync(campaignId);
        }

        // üîÅ Private: Retry a specific log
        private async Task<bool> RetrySendLogAsync(Guid logId)
        {
            var log = await _context.CampaignSendLogs.FirstOrDefaultAsync(x => x.Id == logId);

            if (log == null || log.SendStatus != "Failed" || !log.AllowRetry)
                return false;

            // üîÑ Simulate re-send (replace with actual IMessageService.SendAsync later)
            bool sent = SimulateSendMessage(log);

            log.RetryCount += 1;
            log.LastRetryAt = DateTime.UtcNow;
            log.LastRetryStatus = sent ? "Sent" : "Failed";
            log.SendStatus = sent ? "Sent" : "Failed";
            log.ErrorMessage = sent ? null : "Mock failure on retry";

            await _context.SaveChangesAsync();
            return sent;
        }

        // üîÅ Private: Retry all failed logs in a given campaign
        private async Task<int> RetryAllFailedInCampaignAsync(Guid campaignId)
        {
            var failedLogs = await _context.CampaignSendLogs
                .Where(log => log.CampaignId == campaignId && log.SendStatus == "Failed" && log.AllowRetry)
                .ToListAsync();

            int successCount = 0;

            foreach (var log in failedLogs)
            {
                bool sent = SimulateSendMessage(log);

                log.RetryCount += 1;
                log.LastRetryAt = DateTime.UtcNow;
                log.LastRetryStatus = sent ? "Sent" : "Failed";
                log.SendStatus = sent ? "Sent" : "Failed";
                log.ErrorMessage = sent ? null : "Mock failure on retry";

                if (sent) successCount++;
            }

            await _context.SaveChangesAsync();
            return successCount;
        }

        // üîß Simulated send (replace with actual WhatsApp message logic)
        private bool SimulateSendMessage(CampaignSendLog log)
        {
            return new Random().NextDouble() < 0.9; // 90% success rate
        }
    }
}

// üìÑ Features/CampaignTracking/Services/ClickTokenService.cs
using System.Security.Cryptography;
using System.Text;
using System.Text.Json;
using Microsoft.AspNetCore.WebUtilities;
using Microsoft.Extensions.Options;
using xbytechat.api.Features.CampaignTracking.Config;

namespace xbytechat.api.Features.CampaignTracking.Services
{
    public record ClickTokenPayload(
        Guid cid,            // CampaignSendLogId
        int bi,              // button index
        string bt,           // button title (optional)
        string to,           // destination absolute URL
        long iat,            // issued at (unix seconds)
        long exp             // expiry (unix seconds)
    );

    public interface IClickTokenService
    {
        string Create(ClickTokenPayload payload);
        bool TryValidate(string token, out ClickTokenPayload? payload, out string? error);
    }

    public class ClickTokenService : IClickTokenService
    {
        private readonly byte[] _key;

        public ClickTokenService(IOptions<TrackingOptions> opt)
        {
            _key = Encoding.UTF8.GetBytes(opt.Value.Secret ?? throw new ArgumentNullException(nameof(opt.Value.Secret)));
        }

        public string Create(ClickTokenPayload payload)
        {
            var json = JsonSerializer.Serialize(payload);
            var data = Encoding.UTF8.GetBytes(json);
            var body = WebEncoders.Base64UrlEncode(data);

            var sig = ComputeHmac(body);
            return $"{body}.{sig}";
        }

        public bool TryValidate(string token, out ClickTokenPayload? payload, out string? error)
        {
            payload = null;
            error = null;

            var parts = token.Split('.');
            if (parts.Length != 2) { error = "format"; return false; }

            var body = parts[0];
            var sig = parts[1];

            var expected = ComputeHmac(body);
            // timing-safe compare
            if (!CryptographicOperations.FixedTimeEquals(Encoding.UTF8.GetBytes(sig), Encoding.UTF8.GetBytes(expected)))
            { error = "bad-signature"; return false; }

            try
            {
                var bytes = WebEncoders.Base64UrlDecode(body);
                var obj = JsonSerializer.Deserialize<ClickTokenPayload>(bytes);
                if (obj is null) { error = "decode"; return false; }

                var now = DateTimeOffset.UtcNow.ToUnixTimeSeconds();
                if (now > obj.exp) { error = "expired"; return false; }

                // minimal URL sanity check
                if (!Uri.TryCreate(obj.to, UriKind.Absolute, out var _)) { error = "bad-destination"; return false; }

                payload = obj;
                return true;
            }
            catch (Exception ex)
            {
                error = "exception:" + ex.GetType().Name;
                return false;
            }
        }

        private string ComputeHmac(string body)
        {
            using var h = new HMACSHA256(_key);
            var sig = h.ComputeHash(Encoding.UTF8.GetBytes(body));
            return WebEncoders.Base64UrlEncode(sig);
        }
    }
}

