using System;
using System.Collections.Generic;
using System.Linq;
using System.Text.Json;
using System.Text.RegularExpressions;
using System.Threading;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using Microsoft.EntityFrameworkCore;
using xbytechat.api;
using xbytechat.api.Shared;
using xbytechat_api.WhatsAppSettings.Services; // User.GetBusinessId()

namespace xbytechat.api.Features.CsvModule.Controllers
{
    [ApiController]
    [Route("api/csv/batch/{batchId:guid}/validate")]
    [Authorize]
    public sealed class CsvBatchValidationController : ControllerBase
    {
        private readonly AppDbContext _db;
        public CsvBatchValidationController(AppDbContext db) => _db = db;

        public sealed class ValidateRequest
        {
            public string? PhoneHeader { get; set; }              // e.g. "phone"
            public List<string>? RequiredHeaders { get; set; }    // e.g. ["parameter1","headerpara1","buttonpara1"]
            public bool NormalizePhone { get; set; } = true;
            public bool CheckDuplicates { get; set; } = true;
            public int? Limit { get; set; }                       // optional sample cap
        }

        public sealed class ValidateResponse
        {
            public bool Success { get; set; } = true;
            public List<string> Problems { get; set; } = new();
            public object Stats { get; set; } = new { rows = 0, missingPhone = 0, invalidPhones = 0, duplicatePhones = 0 };
            public List<string> Headers { get; set; } = new();    // discovered headers in the batch
        }

        [HttpPost]
        public async Task<IActionResult> Validate(Guid batchId, [FromBody] ValidateRequest req, CancellationToken ct = default)
        {
            var businessId = User.GetBusinessId();
            if (businessId == Guid.Empty) return Unauthorized();

            // Load CSV rows for this batch (owned by business)
            var rowsQ = _db.CsvRows
                .AsNoTracking()
                .Where(r => r.BusinessId == businessId && r.BatchId == batchId)
                .OrderBy(r => r.RowIndex);

            var total = await rowsQ.CountAsync(ct);
            if (total == 0)
                return Ok(new ValidateResponse
                {
                    Problems = new List<string> { "CSV batch is empty." },
                    Stats = new { rows = 0, missingPhone = 0, invalidPhones = 0, duplicatePhones = 0 },
                    Headers = new List<string>()
                });

            var rows = req.Limit.HasValue && req.Limit.Value > 0
                ? await rowsQ.Take(req.Limit.Value).ToListAsync(ct)
                : await rowsQ.ToListAsync(ct);

            // Discover header set by union of row keys (case-insensitive compare)
            var headerSet = new HashSet<string>(StringComparer.OrdinalIgnoreCase);
            foreach (var r in rows)
                foreach (var k in KeysOfJson(r.DataJson))
                    headerSet.Add(k);

            var headers = headerSet.OrderBy(x => x, StringComparer.OrdinalIgnoreCase).ToList();

            var problems = new List<string>();

            // Validate phone header presence
            var phoneHeader = (req.PhoneHeader ?? "").Trim();
            if (string.IsNullOrWhiteSpace(phoneHeader))
            {
                // Try helpful guesses
                var guesses = new[] { "phone", "mobile", "whatsapp", "number", "phonee164", "msisdn", "whatsapp_number" };
                var guess = guesses.FirstOrDefault(h => headerSet.Contains(h));
                if (!string.IsNullOrEmpty(guess))
                    phoneHeader = guess;
            }

            if (string.IsNullOrWhiteSpace(phoneHeader))
            {
                problems.Add("Phone column not specified and could not be guessed.");
            }
            else if (!headerSet.Contains(phoneHeader))
            {
                problems.Add($"Phone column ‚Äú{phoneHeader}‚Äù not found in CSV.");
            }

            // Validate requiredHeaders presence (parameterN/headerparaN/buttonparaN)
            var required = req.RequiredHeaders ?? new List<string>();
            foreach (var h in required)
            {
                if (!headerSet.Contains(h))
                    problems.Add($"Required column ‚Äú{h}‚Äù is missing.");
            }

            // Row-level checks
            int missingPhone = 0, invalidPhones = 0, duplicatePhones = 0;
            var seen = new HashSet<string>(StringComparer.Ordinal);

            foreach (var r in rows)
            {
                var dict = JsonToDict(r.DataJson);

                // phone
                string? rawPhone = null;
                if (!string.IsNullOrWhiteSpace(phoneHeader))
                    dict.TryGetValue(phoneHeader, out rawPhone);

                var normPhone = NormalizePhoneMaybe(rawPhone, req.NormalizePhone);
                if (string.IsNullOrWhiteSpace(normPhone))
                {
                    missingPhone++;
                    continue;
                }

                // naive validity check
                if (!Regex.IsMatch(normPhone, @"^\d{10,15}$"))
                {
                    invalidPhones++;
                }

                if (req.CheckDuplicates && !seen.Add(normPhone))
                {
                    duplicatePhones++;
                }
            }

            var resp = new ValidateResponse
            {
                Problems = problems,
                Stats = new { rows = total, missingPhone, invalidPhones, duplicatePhones },
                Headers = headers
            };

            return Ok(resp);
        }

        // ---------- helpers ----------
        private static IEnumerable<string> KeysOfJson(string? json)
        {
            if (string.IsNullOrWhiteSpace(json)) yield break;
            using var doc = JsonDocument.Parse(json);
            if (doc.RootElement.ValueKind != JsonValueKind.Object) yield break;
            foreach (var p in doc.RootElement.EnumerateObject())
                yield return p.Name;
        }

        private static Dictionary<string, string> JsonToDict(string? json)
        {
            var dict = new Dictionary<string, string>(StringComparer.OrdinalIgnoreCase);
            if (string.IsNullOrWhiteSpace(json)) return dict;
            using var doc = JsonDocument.Parse(json);
            if (doc.RootElement.ValueKind != JsonValueKind.Object) return dict;
            foreach (var p in doc.RootElement.EnumerateObject())
                dict[p.Name] = p.Value.ValueKind == JsonValueKind.Null ? "" : p.Value.ToString();
            return dict;
        }

        private static string? NormalizePhoneMaybe(string? raw, bool normalize)
        {
            if (string.IsNullOrWhiteSpace(raw)) return null;
            var trimmed = raw.Trim();
            if (!normalize) return trimmed;

            // simple E.164-ish cleanup
            var digits = Regex.Replace(trimmed, "[^0-9]", "");
            digits = digits.TrimStart('0');
            if (digits.Length == 10) digits = "91" + digits; // heuristic India
            return digits;
        }
    }
}

using Microsoft.AspNetCore.Mvc;
using Serilog;
using xbytechat.api.Features.CTAFlowBuilder.DTOs;
using xbytechat.api.Features.CTAFlowBuilder.Services;

namespace xbytechat.api.Features.CTAFlowBuilder.Controllers
{
    [ApiController]
    [Route("api/cta-flow")]
    public class CTAFlowController : ControllerBase
    {
        private readonly ICTAFlowService _flowService;

        public CTAFlowController(ICTAFlowService flowService)
        {
            _flowService = flowService;
        }

        // CREATE (draft-only)
        [HttpPost("save-visual")]
        public async Task<IActionResult> SaveVisualFlow([FromBody] SaveVisualFlowDto dto)
        {
            var businessIdClaim = User.FindFirst("businessId")?.Value;
            var createdBy = User.FindFirst("name")?.Value ?? "system";
            if (!Guid.TryParse(businessIdClaim, out var businessId))
                return BadRequest(new { message = "‚ùå Invalid business ID" });

            Log.Information("üì¶ Saving CTA Flow: {FlowName} by {User}", dto.FlowName, createdBy);

            var result = await _flowService.SaveVisualFlowAsync(dto, businessId, createdBy);
            if (!result.Success)
            {
                var m = (result.ErrorMessage ?? "").Trim();

                // map common validation/conflict by message text (no result.Code available)
                if (m.Contains("already exists", StringComparison.OrdinalIgnoreCase))
                    return Conflict(new { message = "‚ùå Duplicate flow name", error = m });

                if (m.Contains("required", StringComparison.OrdinalIgnoreCase) ||
                    m.Contains("empty flow", StringComparison.OrdinalIgnoreCase) ||
                    m.Contains("invalid", StringComparison.OrdinalIgnoreCase))
                    return BadRequest(new { message = "‚ùå Failed to save flow", error = m });

                return StatusCode(StatusCodes.Status500InternalServerError,
                    new { message = "‚ùå Failed to save flow", error = string.IsNullOrWhiteSpace(m) ? "Unknown error" : m });
            }

            Guid? flowId = null;
            if (result.Data is not null)
            {
                try { dynamic d = result.Data; flowId = (Guid?)d.flowId; } catch { }
            }

            return Ok(new { message = "‚úÖ Flow saved successfully", flowId });
        }

        // PUBLISH (by id)
        [HttpPost("{id:guid}/publish")]
        public async Task<IActionResult> Publish(Guid id)
        {
            var biz = User.FindFirst("businessId")?.Value;
            var user = User.FindFirst("name")?.Value ?? "system";
            if (!Guid.TryParse(biz, out var businessId))
                return BadRequest(new { message = "‚ùå Invalid business." });

            var ok = await _flowService.PublishFlowAsync(id, businessId, user);
            return ok ? Ok(new { message = "‚úÖ Flow published." }) : NotFound(new { message = "‚ùå Flow not found." });
        }

        // DELETE (only if not attached)
        [HttpDelete("{id:guid}")]
        public async Task<IActionResult> Delete(Guid id)
        {
            var biz = User.FindFirst("businessId")?.Value;
            if (!Guid.TryParse(biz, out var businessId))
                return BadRequest(new { message = "‚ùå Invalid business." });

            var deletedBy = User.FindFirst("name")?.Value
                          ?? User.FindFirst("email")?.Value
                          ?? User.FindFirst("sub")?.Value
                          ?? "system";

            var result = await _flowService.DeleteFlowAsync(id, businessId, deletedBy);

            if (!result.Success)
            {
                var msg = (result.ErrorMessage ?? result.Message ?? string.Empty).Trim();

                // If message says it's attached, return 409 and include campaigns for the modal
                if (msg.Contains("attached", StringComparison.OrdinalIgnoreCase) ||
                    msg.Contains("Cannot delete", StringComparison.OrdinalIgnoreCase))
                {
                    var campaigns = await _flowService.GetAttachedCampaignsAsync(id, businessId);
                    return Conflict(new { message = msg, campaigns });
                }

                if (msg.Contains("not found", StringComparison.OrdinalIgnoreCase))
                    return NotFound(new { message = msg });

                return BadRequest(new { message = string.IsNullOrWhiteSpace(msg) ? "Delete failed." : msg });
            }

            return Ok(new { message = result.Message ?? "‚úÖ Flow deleted." });
        }

        // LISTS
        [HttpGet("all-published")]
        public async Task<IActionResult> GetPublishedFlows()
        {
            var businessIdClaim = User.FindFirst("businessId")?.Value;
            if (!Guid.TryParse(businessIdClaim, out var businessId))
                return BadRequest(new { message = "‚ùå Invalid business ID" });

            var flows = await _flowService.GetAllPublishedFlowsAsync(businessId);
            return Ok(flows);
        }

        [HttpGet("all-draft")]
        public async Task<IActionResult> GetAllDraftFlows()
        {
            var businessIdClaim = User.FindFirst("businessId")?.Value;
            if (!Guid.TryParse(businessIdClaim, out var businessId))
                return BadRequest(new { message = "‚ùå Invalid business ID" });

            var flows = await _flowService.GetAllDraftFlowsAsync(businessId);
            return Ok(flows);
        }

        // DETAIL
        [HttpGet("by-id/{id:guid}")]
        public async Task<IActionResult> GetById(Guid id)
        {
            var businessIdClaim = User.FindFirst("businessId")?.Value;
            if (!Guid.TryParse(businessIdClaim, out var businessId))
                return BadRequest(new { message = "‚ùå Invalid business ID" });

            var dto = await _flowService.GetVisualFlowByIdAsync(id, businessId);
            if (dto is null) return NotFound(new { message = "‚ùå Flow not found." });

            return Ok(dto);
        }

        [HttpGet("visual/{id:guid}")]
        public async Task<IActionResult> GetVisualFlow(Guid id)
        {
            var businessIdClaim = User.FindFirst("businessId")?.Value;
            if (!Guid.TryParse(businessIdClaim, out var businessId))
                return BadRequest(new { message = "‚ùå Invalid business ID" });

            var result = await _flowService.GetVisualFlowAsync(id, businessId);
            if (!result.Success)
            {
                var m = (result.ErrorMessage ?? string.Empty).Trim();
                if (m.Contains("not found", StringComparison.OrdinalIgnoreCase))
                    return NotFound(new { message = "‚ùå Failed to load flow", error = m });

                return StatusCode(StatusCodes.Status500InternalServerError,
                    new { message = "‚ùå Failed to load flow", error = string.IsNullOrWhiteSpace(m) ? "Unknown error" : m });
            }

            return Ok(result.Data);
        }

        // USAGE (for delete guard)
        [HttpGet("{id:guid}/usage")]
        public async Task<IActionResult> GetUsage(Guid id)
        {
            var biz = User.FindFirst("businessId")?.Value;
            if (!Guid.TryParse(biz, out var businessId))
                return BadRequest(new { message = "Invalid business." });

            var campaigns = await _flowService.GetAttachedCampaignsAsync(id, businessId);
            return Ok(new
            {
                canDelete = campaigns.Count == 0,
                count = campaigns.Count,
                campaigns
            });
        }
    }
}


