using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using Microsoft.EntityFrameworkCore;
using Microsoft.Extensions.FileSystemGlobbing;
using xbytechat_api.WhatsAppSettings.Models;
using xbytechat_api.WhatsAppSettings.Services;
namespace xbytechat.api.WhatsAppSettings.Controllers
{
    [ApiController]
    [Route("api/templates")]
    public class TemplatesController : ControllerBase
    {
        private readonly AppDbContext _db;
        private readonly ITemplateSyncService _sync;
        private readonly IWhatsAppTemplateFetcherService _fetcher;

        public TemplatesController(AppDbContext db, ITemplateSyncService sync, IWhatsAppTemplateFetcherService fetcher)
        { _db = db; _sync = sync; _fetcher = fetcher; }

        [HttpPost("sync/{businessId:guid}")]
        [Authorize]
        public async Task<IActionResult> Sync(Guid businessId, [FromQuery] bool force = false)
        {
            if (businessId == Guid.Empty) return BadRequest(new { success = false, message = "Invalid businessId" });
            var result = await _sync.SyncBusinessTemplatesAsync(businessId, force);
            return Ok(new { success = true, result });
        }

        [HttpGet("{businessId:guid}")]
        [Authorize]
        public async Task<IActionResult> List(Guid businessId, [FromQuery] string? q = null,
            [FromQuery] string? status = "APPROVED", [FromQuery] string? language = null,
            [FromQuery] string? provider = null)
        {
            var query = _db.WhatsAppTemplates.AsNoTracking()
                .Where(x => x.BusinessId == businessId && x.IsActive);

            if (!string.IsNullOrWhiteSpace(status))
                query = query.Where(x => x.Status == status);

            if (!string.IsNullOrWhiteSpace(language))
                query = query.Where(x => x.Language == language);

            if (!string.IsNullOrWhiteSpace(provider))
                query = query.Where(x => x.Provider == provider);

            if (!string.IsNullOrWhiteSpace(q))
                query = query.Where(x => x.Name.Contains(q) || x.Body.Contains(q));

            var items = await query
                .OrderBy(x => x.Name)
                .Select(x => new
                {
                    x.Name,
                    x.Language,
                    x.Status,
                    x.Category,
                    x.PlaceholderCount,
                    x.HasImageHeader,
                    x.ButtonsJson
                })
                .ToListAsync();

            return Ok(new { success = true, templates = items });
        }

        [HttpGet("{businessId:guid}/{name}")]
        [Authorize]
        //public async Task<IActionResult> GetOne(Guid businessId, string name, [FromQuery] string? language = null)
        //{
        //    var tpl = await _db.WhatsAppTemplates.AsNoTracking()
        //        .FirstOrDefaultAsync(x => x.BusinessId == businessId && x.Name == name &&
        //                                  (language == null || x.Language == language));
        //    if (tpl == null) return NotFound();
        //    return Ok(new
        //    {
        //        tpl.Name,
        //        tpl.Language,
        //        tpl.Status,
        //        tpl.Category,
        //        tpl.Body,
        //        tpl.PlaceholderCount,
        //        tpl.HasImageHeader,
        //        tpl.ButtonsJson
        //    });
        //}

        public async Task<IActionResult> GetOne(Guid businessId, string name, [FromQuery] string? language = null)
        {
            var tpl = await _db.WhatsAppTemplates.AsNoTracking()
                .FirstOrDefaultAsync(x => x.BusinessId == businessId && x.Name == name &&
                                          (language == null || x.Language == language));
            if (tpl == null) return NotFound();

            // üîé Ask meta service for precise header info (covers IMAGE/VIDEO/DOCUMENT/TEXT/none)
            string headerKind = "none";
            bool requiresHeaderMediaUrl = false;
            try
            {
                var meta = await _fetcher.GetTemplateMetaAsync(businessId, tpl.Name, tpl.Language, provider: null);
                var ht = meta?.HeaderType?.ToUpperInvariant();
                headerKind = ht switch
                {
                    "IMAGE" => "image",
                    "VIDEO" => "video",
                    "DOCUMENT" => "document",
                    "TEXT" => "text",
                    _ => (tpl.HasImageHeader ? "image" : "none")
                };
                requiresHeaderMediaUrl = headerKind is "image" or "video" or "document";
            }
            catch
            {
                // fallback to legacy flag
                headerKind = tpl.HasImageHeader ? "image" : "none";
                requiresHeaderMediaUrl = headerKind == "image";
            }

            return Ok(new
            {
                tpl.Name,
                tpl.Language,
                tpl.Status,
                tpl.Category,
                tpl.Body,
                tpl.PlaceholderCount,
                tpl.HasImageHeader,
                tpl.ButtonsJson,
                headerKind,                // üëà NEW for UI
                requiresHeaderMediaUrl     // üëà NEW for UI
            });
        }
    }
}

using System;
using System.Linq;
using System.Security.Claims;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Mvc;
using Microsoft.AspNetCore.Http;
using Microsoft.Extensions.Logging;
using xbytechat.api.Features.WhatsAppSettings.Services;
using xbytechat.api.Features.WhatsAppSettings.Models;

namespace xbytechat.api.Features.WhatsAppSettings.Controllers
{
    [ApiController]
    [Route("api/whatsappsettings/{provider}/numbers")]
    public class WhatsAppNumbersController : ControllerBase
    {
        private readonly IWhatsAppPhoneNumberService _svc;
        private readonly ILogger<WhatsAppNumbersController> _logger;

        public WhatsAppNumbersController(
            IWhatsAppPhoneNumberService svc,
            ILogger<WhatsAppNumbersController> logger)
        {
            _svc = svc;
            _logger = logger;
        }

        // Helper to resolve BusinessId from claims/header/query (adjust to your auth)
        private bool TryGetBusinessId(out Guid businessId)
        {
            businessId = Guid.Empty;

            // 1) Claim (preferred)
            var claim = User?.FindFirst("BusinessId") ?? User?.FindFirst("businessId");
            if (claim != null && Guid.TryParse(claim.Value, out businessId))
                return true;

            // 2) Header fallback
            if (Request.Headers.TryGetValue("X-Business-Id", out var h)
                && Guid.TryParse(h.ToString(), out businessId))
                return true;

            // 3) Query fallback
            if (Guid.TryParse(HttpContext.Request.Query["businessId"], out businessId))
                return true;

            return false;
        }

        // GET /api/whatsappsettings/{provider}/numbers
        [HttpGet]
        [ProducesResponseType(StatusCodes.Status200OK)]
        [ProducesResponseType(StatusCodes.Status400BadRequest)]
        public async Task<IActionResult> List([FromRoute] string provider)
        {
            if (!TryGetBusinessId(out var businessId))
                return BadRequest("BusinessId is required (claim/header/query).");

            var items = await _svc.ListAsync(businessId, provider);
            return Ok(items);
        }

        public sealed class UpsertRequest
        {
            public string PhoneNumberId { get; set; } = null!;
            public string WhatsAppBusinessNumber { get; set; } = null!;
            public string? SenderDisplayName { get; set; }
            public bool? IsActive { get; set; }
            public bool? IsDefault { get; set; }
        }

        // POST /api/whatsappsettings/{provider}/numbers
        [HttpPost("")]
        [ProducesResponseType(typeof(WhatsAppPhoneNumber), StatusCodes.Status200OK)]
        [ProducesResponseType(StatusCodes.Status400BadRequest)]
       // [HttpPost("")]
        public async Task<IActionResult> Upsert([FromRoute] string provider, [FromBody] UpsertRequest req)
        {
            if (!TryGetBusinessId(out var businessId))
                return BadRequest("BusinessId is required (claim/header/query).");

            if (string.IsNullOrWhiteSpace(req.PhoneNumberId))
                return BadRequest("phoneNumberId is required.");
            if (string.IsNullOrWhiteSpace(req.WhatsAppBusinessNumber))
                return BadRequest("whatsAppBusinessNumber is required.");

            try
            {
                var saved = await _svc.UpsertAsync(
                    businessId,
                    provider, // service normalizes
                    req.PhoneNumberId,
                    req.WhatsAppBusinessNumber,
                    req.SenderDisplayName,
                    req.IsActive,
                    req.IsDefault
                );
                return Ok(saved);
            }
            catch (InvalidOperationException ex) when (ex.InnerException is Npgsql.NpgsqlException npg)
            {
                // Bubble up constraint codes if useful
                // 23505 unique_violation, 23503 foreign_key_violation, 23502 not_null_violation
                return StatusCode(StatusCodes.Status409Conflict, new
                {
                    statusCode = 409,
                    code = npg.SqlState,
                    message = ex.Message
                });
            }
            catch (Exception ex)
            {
                return StatusCode(StatusCodes.Status500InternalServerError, new
                {
                    statusCode = 500,
                    message = ex.Message
                });
            }
        }

        // DELETE /api/whatsappsettings/{provider}/numbers/{id}
        [HttpDelete("{id:guid}")]
        [ProducesResponseType(StatusCodes.Status204NoContent)]
        [ProducesResponseType(StatusCodes.Status404NotFound)]
        [ProducesResponseType(StatusCodes.Status400BadRequest)]
        public async Task<IActionResult> Delete([FromRoute] string provider, [FromRoute] Guid id)
        {
            if (!TryGetBusinessId(out var businessId))
                return BadRequest("BusinessId is required (claim/header/query).");

            var ok = await _svc.DeleteAsync(businessId, provider, id);
            if (!ok) return NotFound();

            return NoContent();
        }

        // PATCH /api/whatsappsettings/{provider}/numbers/{id}/default
        [HttpPatch("{id:guid}/default")]
        [ProducesResponseType(StatusCodes.Status204NoContent)]
        [ProducesResponseType(StatusCodes.Status404NotFound)]
        [ProducesResponseType(StatusCodes.Status400BadRequest)]
        public async Task<IActionResult> SetDefault([FromRoute] string provider, [FromRoute] Guid id)
        {
            if (!TryGetBusinessId(out var businessId))
                return BadRequest("BusinessId is required (claim/header/query).");

            var ok = await _svc.SetDefaultAsync(businessId, provider, id);
            if (!ok) return NotFound();

            return NoContent();
        }
    }
}

